import PropTypes from 'prop-types';

export const DEFAULT_PROPS = {
  check: '',
  permissions: []
};

export const PROP_TYPES = {
  check: PropTypes.any,
  children: PropTypes.element,
  permissions: PropTypes.Array
};
import {hasPermission} from 'src/utils/functions';
import {DEFAULT_PROPS, PROP_TYPES} from './Authorize.config';

const Authorize = ({check, children, permissions}) => {
  return (
    check ? (hasPermission(permissions, check) ? children : null) : children
  );
};

Authorize.defaultProps = DEFAULT_PROPS;
Authorize.propTypes = PROP_TYPES;

export {Authorize};
